mapper.select:
 		select * from tbl_review where product_id=#{searchType}
 		order by ${orderBy}
		limit #{pageStart}, #{perPageNum}

 		select * from tbl_review where meterial_id=#{searchType}
 		order by ${orderBy}
		limit #{pageStart}, #{perPageNum}

 		select * from uname_review where review_writer=#{searchType} 

 			order by review_number desc


 			order by review_click desc

		limit #{pageStart}, #{perPageNum}
