mapper.select:
select * from c_list
where user_id=#{user_id}
order by cart_number desc

mapper.update:
update tbl_cart
set cart_product_qtt=cart_product_qtt+#{cart_product_qtt}
where user_id=#{user_id} and product_id=#{product_id}

update tbl_cart
set cart_product_qtt=#{cart_product_qtt}
where user_id=#{user_id} and product_id=#{product_id}
